import logo from './logo.svg';
import './App.css';
import {userState} from 'react'
import {ethers} from 'ethers'
import XContract from '././artifacts/contracts/XContract.sol/XContract.json/'

const contractAddress = '0x88598465cA23e28D69A35F9435DDb4567809E4f3'

function App() {
  const [greeting, balance, setGreetingValue] = userState()

  async function requestAccount(){

  }
  async function fetchGreeting(){
    if (typeof window.ethereum !== 'undefined'){
      const provider = new ethers.providers.Web3Provider(window.ethereum)
      const contract = new ethers.Contract(contractAddress, XContract.abi, provider)
      try{
        const data = await contract.greet("Hello")
        console.log('data: ', data)
      } catch(err){
        console.log("Error: ", err)
      }
    }
  }

  async function setGreeting(){
    if (!greeting) return
    if (typeof window.ethereum !== 'undefined'){
      await requestAccount()
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, XContract.abi, signer)
      const transaction = await contract.setGreeting(greeting)
      setGreeting('')
      await transaction.wait()
      fetchGreeting()
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={fetchGreeting}> Fetch Greeting </button> 
        <button onClick={setGreeting}> Set Greeting </button>

        <input 
          onChange={e => setGreetingValue(e.target.value)} 
          placeholder="Set greeting"
          value={greeting}
        />

      </header>
    </div>
  );
}

export default App;
