import React from 'react';
import { Document, Page } from 'react-pdf';
import {useState, useEffect} from 'react'
import '@react-pdf-viewer/core/lib/styles/index.css'
import '@react-pdf-viewer/default-layout/lib/styles/index.css'
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import pdfFile from "./Research_Proposal.pdf"
import {Worker, Viewer} from '@react-pdf-viewer/core'

function Reports() {
  const pdf_url = "./../doc/Research_Proposal.pdf"
  const [defaultPdfFile] = useState(pdfFile)
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }
  const defaultLayoutPluginInstance = defaultLayoutPlugin();

  return (
    <div className='reports'>
      <br></br>
      <h1>Reports</h1>
      {defaultPdfFile&&<><Worker workerUrl="https://unpkg.com/pdfjs-dist@2.6.347/build/pdf.worker.min.js">
          <Viewer fileUrl={defaultPdfFile}
            plugins={[defaultLayoutPluginInstance]} />
        </Worker></>}
      <Document file="Research_Proposal.pdf" onLoadSuccess={onDocumentLoadSuccess} >
          <Page pageNumber={pageNumber} />
      </Document>

    </div>
  );
}

export default Reports;
